import './App.css';
import userInput from './component/userInput';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      user : ''
    }
  }

    changeName = (e) => {
      this.setState({
        user : e.target.value
      })
    }


  render() {
    return(
      <div>
        <UserInput changeName={this.changeName}/>
      </div>
    )
  }
}


export default App;



