  render() {
    return (
      <div className="app">
        <InputName  person={this.state.user} changePerson={this.changePerson} />
        {/* <Sumbit checkInput={this.checkInput}/> */}
        {/* <UserOutput display={this.state.user}/> */}
      </div>
    )
  }
}

export default App
