import React, { useState } from 'react'
// import React, { component} from 'react'
import InputName from './component/userInput'
import UserOutput from './component/userOutput'
import Sumbit from './component/user-submit'

// use function component
const App = () => {
  const [user, setUser] = useState('')
  const [isInformation, setisInformation] = useState(false)
  const checkInput = () => {
    if(this.state.user.length === 0 ) {
      alert('Input tidak boleh kosong')
    } else {
      // alert('Input Berhasil')
        this.state.isInformation = true
  }

  return (
    <div className="app">
    <InputName  person={user} changeName={setUser} />
    <Sumbit checkInput={checkInput}/>
    <UserOutput isInformation ={setisInformation} display={user}/>
  </div>
   )
  }
}

