import React, {useState} from 'react';
import './App.css';

const App = () => {
  const [username, setUserName] = useState('');

  const changeUserName = (e) => {
    let value = e.target.value;
    console.log(value)
    setUserName(value);
    }


  return (
    <div className="App">
           <label for="exampleInputEmail1">User Name</label>
                    <input type="text" placeholder="User Name" value={username} onChange={changeUserName}></input>
    </div>
  );
}

export default App;
